import { useState, useEffect } from "react";
import "./App.css";

const PLAYER_SIZE = 24;
const SPEED = 8;
const MAP_WIDTH = window.innerWidth * 2; // 200vw
const MAP_HEIGHT = window.innerHeight;

function App() {
  const [x, setX] = useState(100);
  const [y, setY] = useState(MAP_HEIGHT / 2);

  useEffect(() => {
    const handleKeyDown = (e) => {
      setX((prevX) => {
        if (e.key === "ArrowLeft") {
          return Math.max(0, prevX - SPEED);
        }
        if (e.key === "ArrowRight") {
          return Math.min(MAP_WIDTH - PLAYER_SIZE, prevX + SPEED);
        }
        return prevX;
      });

      setY((prevY) => {
        if (e.key === "ArrowUp") {
          return Math.max(0, prevY - SPEED);
        }
        if (e.key === "ArrowDown") {
          return Math.min(MAP_HEIGHT - PLAYER_SIZE, prevY + SPEED);
        }
        return prevY;
      });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game">
      <div className="map">
        <img
          src="/assets/images/maps/town.png"
          alt="map"
          className="map-image"
        />

        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{ left: x + "px", top: y + "px" }}
        />
      </div>
    </div>
  );
}

export default App;
