import React, { useState } from "react";
import "./App.css";

export default function App() {
  const [x, setX] = useState(50);
  const [y, setY] = useState(50);
  const [direction, setDirection] = useState("down");

  const move = (dir) => {
    const step = 10;

    if (dir === "up") {
      setY((prev) => prev - step);
      setDirection("up");
    }
    if (dir === "down") {
      setY((prev) => prev + step);
      setDirection("down");
    }
    if (dir === "left") {
      setX((prev) => prev - step);
      setDirection("left");
    }
    if (dir === "right") {
      setX((prev) => prev + step);
      setDirection("right");
    }
  };

  return (
    <div className="game-container">
      <h1>ðŸŽ® Dot RPG</h1>

      <div className="field">
        <img
          src={`/assets/images/player/${direction}.png`}
          alt="player"
          className="player"
          style={{
            top: y + "px",
            left: x + "px",
          }}
        />
      </div>

      <div className="controls">
        <button onClick={() => move("up")}>â¬†</button>
        <button onClick={() => move("left")}>â¬…</button>
        <button onClick={() => move("right")}>âž¡</button>
        <button onClick={() => move("down")}>â¬‡</button>
      </div>
    </div>
  );
}
