import { useState, useEffect, useRef } from "react";
import "./App.css";

const SPEED = 16;
const PLAYER_SIZE = 48;

function App() {
  const mapRef = useRef(null);

  const [x, setX] = useState(100);
  const [y, setY] = useState(100);

  useEffect(() => {
    const handleKeyDown = (e) => {
      const map = mapRef.current;
      if (!map) return;

      const maxX = map.clientWidth - PLAYER_SIZE;
      const maxY = map.clientHeight - PLAYER_SIZE;

      setX((prev) => {
        if (e.key === "ArrowLeft") return Math.max(0, prev - SPEED);
        if (e.key === "ArrowRight") return Math.min(maxX, prev + SPEED);
        return prev;
      });

      setY((prev) => {
        if (e.key === "ArrowUp") return Math.max(0, prev - SPEED);
        if (e.key === "ArrowDown") return Math.min(maxY, prev + SPEED);
        return prev;
      });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game">
      <div className="map" ref={mapRef}>
        {/* 建物 */}
        <img
          src="/assets/images/buildings/house.png"
          alt="house"
          className="building"
          style={{ left: 300, top: 200 }}
        />

        {/* プレイヤー */}
        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{ left: x, top: y }}
        />
      </div>
    </div>
  );
}

export default App;
