import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [x, setX] = useState(240);
  const [y, setY] = useState(240);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "ArrowUp") setY((y) => y - 10);
      if (e.key === "ArrowDown") setY((y) => y + 10);
      if (e.key === "ArrowLeft") setX((x) => x - 10);
      if (e.key === "ArrowRight") setX((x) => x + 10);
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game">
      <div className="map">
        <img
          src="/assets/images/maps/town.png"
          alt="map"
          className="map-image"
        />

        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{ top: y + "px", left: x + "px" }}
        />
      </div>
    </div>
  );
}

export default App;
