import { useState, useEffect } from "react";
import "./App.css";

const SPEED = 6;

function App() {
  const [x, setX] = useState(window.innerWidth / 2);
  const [y, setY] = useState(window.innerHeight / 2);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === "ArrowLeft") setX((x) => x - SPEED);
      if (e.key === "ArrowRight") setX((x) => x + SPEED);
      if (e.key === "ArrowUp") setY((y) => y - SPEED);
      if (e.key === "ArrowDown") setY((y) => y + SPEED);
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game">
      <div className="map">
        {/* 背景 */}
        <img
          src="/assets/images/maps/town.png"
          alt="map"
          className="map-bg"
        />

        {/* プレイヤー */}
        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{ left: x, top: y }}
        />
      </div>
    </div>
  );
}

export default App;
