import { useState, useEffect } from "react";
import "./App.css";

const PLAYER_SIZE = 24;
const SPEED = 8;

const SCREEN_WIDTH = window.innerWidth;
const SCREEN_HEIGHT = window.innerHeight;
const MAP_WIDTH = SCREEN_WIDTH * 2;

function App() {
  // ワールド座標
  const [playerX, setPlayerX] = useState(200);
  const [playerY, setPlayerY] = useState(SCREEN_HEIGHT / 2);

  useEffect(() => {
    const handleKeyDown = (e) => {
      setPlayerX((x) => {
        if (e.key === "ArrowLeft") {
          return Math.max(0, x - SPEED);
        }
        if (e.key === "ArrowRight") {
          return Math.min(MAP_WIDTH - PLAYER_SIZE, x + SPEED);
        }
        return x;
      });

      setPlayerY((y) => {
        if (e.key === "ArrowUp") {
          return Math.max(0, y - SPEED);
        }
        if (e.key === "ArrowDown") {
          return Math.min(SCREEN_HEIGHT - PLAYER_SIZE, y + SPEED);
        }
        return y;
      });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  // カメラは「プレイヤーを追いかける」
  const cameraX = Math.min(
    Math.max(playerX - SCREEN_WIDTH / 2, 0),
    MAP_WIDTH - SCREEN_WIDTH
  );

  // 描画用座標（ここが肝）
  const drawX = playerX - cameraX;
  const drawY = playerY;

  return (
    <div className="game">
      <div
        className="map"
        style={{
          transform: `translateX(-${cameraX}px)`,
        }}
      >
        <img
          src="/assets/images/maps/town.png"
          alt="map"
          className="map-image"
        />

        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{
            left: drawX,
            top: drawY,
          }}
        />
      </div>
    </div>
  );
}

export default App;
