import { useState, useEffect } from "react";
import "./App.css";

const PLAYER_SIZE = 24;
const SPEED = 6;

function App() {
  const [playerX, setPlayerX] = useState(window.innerWidth / 2);
  const [playerY, setPlayerY] = useState(window.innerHeight / 2);

  useEffect(() => {
    const handleKeyDown = (e) => {
      setPlayerX((x) => {
        if (e.key === "ArrowLeft") return x - SPEED;
        if (e.key === "ArrowRight") return x + SPEED;
        return x;
      });

      setPlayerY((y) => {
        if (e.key === "ArrowUp") return y - SPEED;
        if (e.key === "ArrowDown") return y + SPEED;
        return y;
      });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game">
      <div className="map">
        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{
            left: playerX,
            top: playerY,
          }}
        />
      </div>
    </div>
  );
}

export default App;
