  const move = (direction) => {
    const step = 10;
    if (direction === "up") setY((prev) => prev - step);
    if (direction === "down") setY((prev) => prev + step);
    if (direction === "left") setX((prev) => prev - step);
    if (direction === "right") setX((prev) => prev + step);
  };

