import { useState, useEffect } from "react";
import "./App.css";

const PLAYER_SIZE = 24;
const SPEED = 8;

const SCREEN_WIDTH = window.innerWidth;
const SCREEN_HEIGHT = window.innerHeight;
const MAP_WIDTH = SCREEN_WIDTH * 2;

// カメラの追従率（0.0〜1.0）
const CAMERA_FOLLOW_RATE = 0.15;

function App() {
  // ワールド座標
  const [playerX, setPlayerX] = useState(200);
  const [playerY, setPlayerY] = useState(SCREEN_HEIGHT / 2);
  const [cameraX, setCameraX] = useState(0);

  useEffect(() => {
    const handleKeyDown = (e) => {
      let moved = false;

      setPlayerX((x) => {
        let next = x;
        if (e.key === "ArrowLeft") {
          next = Math.max(0, x - SPEED);
          moved = true;
        }
        if (e.key === "ArrowRight") {
          next = Math.min(MAP_WIDTH - PLAYER_SIZE, x + SPEED);
          moved = true;
        }
        return next;
      });

      setPlayerY((y) => {
        let next = y;
        if (e.key === "ArrowUp") {
          next = Math.max(0, y - SPEED);
          moved = true;
        }
        if (e.key === "ArrowDown") {
          next = Math.min(SCREEN_HEIGHT - PLAYER_SIZE, y + SPEED);
          moved = true;
        }
        return next;
      });

      // プレイヤーが動いたらカメラも少し追従
      if (moved) {
        setCameraX((c) => {
          const target = playerX - SCREEN_WIDTH / 2;
          const next = c + (target - c) * CAMERA_FOLLOW_RATE;

          return Math.min(
            Math.max(next, 0),
            MAP_WIDTH - SCREEN_WIDTH
          );
        });
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [playerX]);

  // 描画用座標
  const drawX = playerX - cameraX;
  const drawY = playerY;

  return (
    <div className="game">
      <div
        className="map"
        style={{
          transform: `translateX(-${cameraX}px)`,
        }}
      >
        <img
          src="/assets/images/maps/town.png"
          alt="map"
          className="map-image"
        />

        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{
            left: drawX,
            top: drawY,
          }}
        />
      </div>
    </div>
  );
}

export default App;
