import { useState, useEffect } from "react";
import "./App.css";

const SPEED = 6;
const PLAYER_SIZE = 24;

function App() {
  const [x, setX] = useState(window.innerWidth / 2);
  const [y, setY] = useState(window.innerHeight / 2);

  useEffect(() => {
    const handleKeyDown = (e) => {
      setX((prevX) => {
        if (e.key === "ArrowLeft") {
          return Math.max(0, prevX - SPEED);
        }
        if (e.key === "ArrowRight") {
          return Math.min(
            window.innerWidth - PLAYER_SIZE,
            prevX + SPEED
          );
        }
        return prevX;
      });

      setY((prevY) => {
        if (e.key === "ArrowUp") {
          return Math.max(0, prevY - SPEED);
        }
        if (e.key === "ArrowDown") {
          return Math.min(
            window.innerHeight - PLAYER_SIZE,
            prevY + SPEED
          );
        }
        return prevY;
      });
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <div className="game">
      <div className="map">
        {/* 背景 */}
        <img
          src="/assets/images/maps/town.png"
          alt="map"
          className="map-bg"
        />

        {/* プレイヤー */}
        <img
          src="/assets/images/player/down.png"
          alt="player"
          className="player"
          style={{ left: x, top: y }}
        />
      </div>
    </div>
  );
}

export default App;
